import React, { Component } from 'react';
import './App.css';
import PostData from './data.json';
import DevList from './DevList';
import SearchBar from './SearchBar';


class App extends Component {
constructor(props) {
  super(props);
  this.state = {
    devs: PostData,
    query: '',
  }
}

  handleInput = (e) => {
    this.setState({ query: e.target.value });
    console.log(e.target.value);
  }


  render() {
    console.log(this.state.query)
    const filteredDevs = this.state.devs.filter(({ languages, tools }) => {

      const normalizedLanguages = languages.map(language => language.toLowerCase());
      const normalizedTools = tools.map(tool => tool.toLowerCase());

      console.log('if statement', normalizedTools.filter(tool => tool.includes(this.state.query.toLowerCase())))
      console.log('if statement lang', normalizedLanguages.filter(language => language.includes(this.state.query.toLowerCase())))
      console.log()
      if ((normalizedTools.filter(tool => tool.includes(this.state.query.toLowerCase()))).length > 0) {
        console.log(normalizedTools);
        return true;
      }  
      else if ((normalizedLanguages.filter(language => language.includes(this.state.query.toLowerCase()))).length > 0){
        return true;
      }
      else {
        return this.state.devs
      }
    })

    console.log('filteredDevs', filteredDevs)
    return(
      <div classname= 'App'>
        <SearchBar
          handleInput={this.handleInput}
        />
        <DevList filteredDevs={filteredDevs}/>

      </div>
    )
  }

}

export default App;
