import React, { Component } from 'react';
import './App.css';
import PostData from './data.json';
import DevList from './DevList';
import FilterChips from './FilterChips';


class App extends Component {
constructor(props) {
  super(props);
  this.state = {
    devs: PostData,
    searchLang: '',
  }
}

  handleClick = () => {
    this.setState({ searchLang: e.target.value });
  }


  render(){

    let filteredDev = this.state.devs.filter((dev) => {
      return dev.languages.tools.includes(this.state.searchLang);
    })


    return(
      <div classname= 'App'>
        <h1>yeah</h1>
        <FilterChips 
          handleClick={this.handleClick} 
        />
        <DevList filteredDevLang={filteredDev}/>

      </div>
    )
  }

}

export default App;
