import React, { Component } from 'react';
import './App.css';
import PostData from './data.json';
import DevList from './DevList';
import SearchBar from './SearchBar';

function startsWith(str, word) {
  return str.lastIndexOf(word, 0) === 0;
}

class App extends Component {
constructor(props) {
  super(props);
  this.state = {
    devs: PostData,
    query: '',
  }
}

  handleInput = (e) => {
    this.setState({ query: e.target.value });
    console.log(e.target.value);
  }


  render() {
    console.log(this.state.query)
    const filteredDevs = this.state.devs.filter(({ languages, tools }) => {
      const normalizedLanguages = languages.map(language => language.toLowerCase());
      console.log('lang ', normalizedLanguages);
      console.log(normalizedLanguages.includes(this.state.query.toLowerCase()))
      const normalizedTools = tools.map(tool => tool.toLowerCase());
      console.log('tools ', normalizedTools);

      if (normalizedTools.map(tool => tool.includes(this.state.query))) {
        return normalizedTools.includes(this.state.query.toLowerCase())
      }
      return normalizedLanguages.includes(this.state.query.toLowerCase()) 
    })

    console.log('filteredDevs', filteredDevs)
    return(
      <div classname= 'App'>
        <SearchBar
          handleInput={this.handleInput}
        />
        <DevList filteredDevs={filteredDevs}/>

      </div>
    )
  }

}

export default App;
