import React, { Component } from 'react';
import './App.css';
import PostData from './data.json';
import DevList from './DevList';
import SearchBar from './SearchBar';


class App extends Component {
constructor(props) {
  super(props);
  this.state = {
    devs: PostData,
    query: '',
  }
}

  handleInput = (e) => {
    this.setState({ query: e.target.value });
    console.log(e.target.value);
  }


  render() {
    const filteredDevs = this.state.devs.filter(({ languages, tools }) => languages.includes(this.state.query) || 
    tools.includes(this.state.query))
    console.log('filtereddevs', filteredDevs)
    return(
      <div classname= 'App'>
        <h1>yeah</h1>
        <SearchBar
          handleInput={this.handleInput}
        />
        <DevList filteredDevs={filteredDevs}/>

      </div>
    )
  }

}

export default App;
