import React, { Component } from 'react';
import './App.css';
import PostData from './data.json';
import DevList from './DevList';
import SearchBar from './SearchBar';


class App extends Component {
constructor(props) {
  super(props);
  this.state = {
    devs: PostData,
    query: '',
  }
}

  handleInput = (e) => {
    this.setState({ query: e.target.value });
    console.log(e.target.value);
  }


  render() {

    if (!String.prototype.startsWith) {
      Object.defineProperty(String.prototype, 'startsWith', {
          value: function(search, rawPos) {
              var pos = rawPos > 0 ? rawPos|0 : 0;
              return this.substring(pos, pos + search.length) === search;
          }
      });
  }

    const filteredDevs = this.state.devs.filter(({ languages, tools }) => {
      const normalizedLanguages = languages.map(language => language.toLowerCase());
      const normalizedTools = tools.map(tool => tool.toLowerCase());
      return normalizedLanguages.startsWith(this.state.query.toLowerCase()) || normalizedTools.startsWith(this.state.query.toLowerCase())
    })

    console.log('filtereddevs', filteredDevs)
    return(
      <div classname= 'App'>
        <SearchBar
          handleInput={this.handleInput}
        />
        <DevList filteredDevs={filteredDevs}/>

      </div>
    )
  }

}

export default App;
