import React, { Component } from 'react';
import './App.css';
import PostData from './data.json';
import DevList from './DevList';
import FilterChips from './FilterChips';


class App extends Component {
constructor(props) {
  super(props);
  this.state = {
    devs: PostData,
    searchLang:  [],
  }
}

  handleClick = () => {
    this.setState({ searchLang: ['JavaScript'] });
  }


  render(){
    const filteredDevs = this.state.searchLang === [] ? this.state.devs : this.state.devs.filter(({ languages }) => languages.includes(this.state.searchLang));

    return(
      <div classname= 'App'>
        <h1>yeah</h1>
        <FilterChips 
          handleClick={this.handleClick}
        />
        <DevList filteredDevs={filteredDevs}/>

      </div>
    )
  }

}

export default App;
