import React, { Component } from 'react';
import './App.css';
import PostData from './data.json';
import DevList from './DevList';
import FilterChips from './FilterChips';


class App extends Component {
constructor(props) {
  super(props);
  this.state = {
    devs: PostData,
    searchLang: '',
  }
}

  handleInput = (e) => {
    this.setState({ searchLang: e.target.value });
    console.log(this.state.searchLang);
  }


  render(){
    let filteredDevs = this.state.devs.filter((dev)=>{
      return dev.languages.includes(this.state.searchLang.toLowerCase())
    })
    console.log('filtereddevs', filteredDevs)
    return(
      <div classname= 'App'>
        <h1>yeah</h1>
        <FilterChips 
          handleInput={this.handleClick}
        />
        <DevList filteredDevs={filteredDevs}/>

      </div>
    )
  }

}

export default App;
