import React, { Component } from 'react';
import './App.css';
import PostData from './data.json';
import DevList from './DevList';
import FilterChips from './FilterChips';


class App extends Component {
constructor(props) {
  super(props);
  this.state = {
    devs: PostData,
    searchLang: '',
  }
}

  handleInput = (e) => {
    this.setState({ searchLang: e.target.value });
    console.log(e.target.value);
  }


  render(){
    let filteredDevs;
    if (this.state.searchLang === "")
      filteredDevs = this.state.devs;
    else if (this.state.devs.filter(({ languages }) => languages.includes(this.state.searchLang)) === "")
      filteredDevs = this.state.devs.filter(({ tools }) => tools.includes(this.state.searchLang));
    else
      filteredDevs = this.state.devs.filter(({ languages }) => languages.includes(this.state.searchLang));

    console.log('filtereddevs', filteredDevs)
    return(
      <div classname= 'App'>
        <h1>yeah</h1>
        <FilterChips 
          handleInput={this.handleInput}
        />
        <DevList filteredDevs={filteredDevs}/>

      </div>
    )
  }

}

export default App;
